import React from "react";

function Article() {
  return (
    <div>
      <form name="moneyForm" class="moneyForm">
        <label for="name">Name :</label>
        <input type="text" id="name" name="name" />
        <br />
        <label for="email">Email :</label>
        <input type="text" id="email" name="email" />
        <br />
        <butt type="submit" value="Submit" name="submit" />
      </form>
    </div>
  );

  let name = document.forms.moneyForm.name.value;
  let email = document.forms.moneyForm.email.value;
  let emailVal = /^([a-z\d\.-]+)@([a-z\d-]+)\.([a-z]{2,8})(\.[a-z]{2,8})?$/;

  function result() {
    if (email === "" || !emailVal.test(email)) {
      alert("This is not a valid email address");
      return false;
    }
    if (!name === "Jedi Jake") {
      alert(
        "Oh man so close....but you didn't win this time! Better luck next time!"
      );
    }
  }
}

export default Article;
